CREATE OR REPLACE FUNCTION obter_seq_inter_prescr_laudo (nr_seq_laudo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_interno_w 	bigint;


BEGIN
	SELECT coalesce(MAX(c.nr_seq_interno),0)
	INTO STRICT	nr_seq_interno_w
	FROM  	laudo_paciente a,
		prescr_procedimento c,
		procedimento_paciente d
	WHERE   d.nr_prescricao = c.nr_prescricao
	AND	d.nr_sequencia_prescricao = c.nr_sequencia
	AND	a.nr_seq_proc = d.nr_sequencia
	AND	a.nr_sequencia = nr_seq_laudo_p;

return	nr_seq_interno_w;

end;
