SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_seq_int_prescr_procmat ( nr_prescricao_p bigint, nr_sequencia_prescricao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
'P'	- prescr_procedimento
'M'	- prescr_material
*/
nr_seq_interno_w	bigint;


BEGIN

if (ie_opcao_p	= 'P') then

	select	max(a.nr_seq_interno)
	into STRICT	nr_seq_interno_w
	from	prescr_procedimento a
	where	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_sequencia	= nr_sequencia_prescricao_p;

elsif (ie_opcao_p	= 'M') then

	select	max(a.nr_seq_interno)
	into STRICT	nr_seq_interno_w
	from	prescr_material a
	where	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_sequencia	= nr_sequencia_prescricao_p;

end if;

return	nr_seq_interno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
