CREATE OR REPLACE FUNCTION obter_seq_item_adep ( nr_seq_cpoe_p bigint, nr_seq_horario_p bigint, nr_prescricoes_p text, nr_prescricao_p bigint, ie_tipo_item_p text) RETURNS bigint AS $body$
