CREATE OR REPLACE FUNCTION obter_seq_item_conta (nr_interno_conta_p bigint, cd_autorizacao_p text, cd_procedimento_p bigint, ie_oridem_proced_p bigint, cd_material_p bigint, dt_item_p timestamp default null) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN

if (coalesce(cd_procedimento_p,0) > 0) and (coalesce(ie_oridem_proced_p,0) > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	procedimento_paciente
	where	nr_interno_conta			= nr_interno_conta_p
	and	coalesce(nr_doc_convenio,cd_autorizacao_p)	= cd_autorizacao_p
	and	cd_procedimento				= cd_procedimento_p
	and	ie_origem_proced			= ie_oridem_proced_p
	and	coalesce(cd_motivo_exc_conta::text, '') = ''
	and	trunc(dt_procedimento)			= trunc(coalesce(dt_item_p, dt_procedimento))
	and	qt_procedimento				> 0;

elsif (coalesce(cd_material_p,0) > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	material_atend_paciente
	where	nr_interno_conta			= nr_interno_conta_p
	and	coalesce(nr_doc_convenio,cd_autorizacao_p)	= cd_autorizacao_p
	and	cd_material				= cd_material_p
	and	coalesce(cd_motivo_exc_conta::text, '') = ''
	and	trunc(dt_atendimento) 			= trunc(coalesce(dt_item_p, dt_atendimento))
	and	qt_material				> 0;
end if;

return 	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
