SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_seq_laudo_prescr ( nr_seq_prescr_p bigint, nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w			bigint;
qtd_w				bigint;


BEGIN

select	count(*)
into STRICT 	qtd_w
from 	laudo_paciente
where 	nr_seq_prescricao 	= nr_seq_prescr_p
and 	nr_prescricao 		= nr_prescricao_p;

if (qtd_w > 0) then
	select 	max(nr_sequencia)
	into STRICT 	nr_sequencia_w
	from 	laudo_paciente
	where 	nr_seq_prescricao 	= nr_seq_prescr_p
	and 	nr_prescricao 		= nr_prescricao_p
	and (ie_status_laudo not in ('LC', 'AC'));
else
	select	max(nr_laudo)
	into STRICT	nr_sequencia_w
	from	procedimento_paciente
	where 	nr_sequencia_prescricao = nr_seq_prescr_p
	and 	nr_prescricao	= nr_prescricao_p
	and		(nr_laudo IS NOT NULL AND nr_laudo::text <> '');
end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
