CREATE OR REPLACE FUNCTION obter_seq_param_conta_contab (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(255);
ds_agrupa_w     varchar(255);

c01 CURSOR FOR
        SELECT  cd_sequencia_parametro
        from    movimento_contabil_doc
        where   nr_seq_ctb_movto = nr_sequencia_p;


BEGIN
    begin
        open c01;
        loop
        fetch c01 into ds_agrupa_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */
                begin
                if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
                        ds_retorno_w := ds_retorno_w || ', ' || ds_agrupa_w;
                else
                        ds_retorno_w := ds_agrupa_w;
                end if;
                end;
        end loop;
        close C01;
        exception
        when others then
            ds_retorno_w        := null;
    end;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
