CREATE OR REPLACE FUNCTION obter_seq_pasta_superior (nr_seq_item_pasta_p bigint, ie_opcao_P text) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	bigint := 0;


BEGIN

if (ie_opcao_P = 'P') then

	SELECT	max(j.nr_seq_pasta)
	into STRICT	nr_retorno_w
	FROM	prontuario_item_pasta j
	WHERE	j.nr_sequencia = 	nr_seq_item_pasta_p;

elsif (ie_opcao_P = 'F') then

	SELECT max(y.nr_seq_pasta_superior)
	into STRICT	nr_retorno_w
	FROM	prontuario_pasta y
	WHERE	y.nr_sequencia IN (
			SELECT	j.nr_seq_pasta
			FROM	prontuario_item_pasta j
			WHERE	j.nr_sequencia = nr_seq_item_pasta_p);

end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
