SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_seq_proced_prescr_relat ( nr_prescricao_p bigint, qt_proced_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_proced_w	integer;
ds_lista_seq_w	varchar(2000) := null;
qt_proced_w	bigint := 0;

c01 CURSOR FOR
SELECT	nr_sequencia
from   	prescr_procedimento
where  	nr_prescricao = nr_prescricao_p
order by
	nr_sequencia;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into nr_seq_proced_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		qt_proced_w := qt_proced_w + 1;

		if (coalesce(ds_lista_seq_w::text, '') = '') then
			begin
			ds_lista_seq_w := nr_seq_proced_w;
			end;
		else	begin
			ds_lista_seq_w := ds_lista_seq_w || ',' || nr_seq_proced_w;
			end;
		end if;

		if (qt_proced_w = qt_proced_p) then
			begin
			qt_proced_w	:= 0;
			ds_lista_seq_w 	:= ds_lista_seq_w || '#@#@';
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;

return ds_lista_seq_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
