CREATE OR REPLACE FUNCTION obter_seq_result_dor ( cd_escala_dor_p text, qt_escala_dor_p bigint) RETURNS bigint AS $body$
DECLARE

nr_sequencia_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	escala_dor_regra a,
		escala_dor_regra_item b
	where	a.nr_sequencia	= b.nr_seq_regra
	and	b.cd_escala_dor	= cd_escala_dor_p
	and	qt_escala_dor_p between QT_PTO_MIN and QT_PTO_MAX
	and	coalesce(a.ie_situacao,'A')	= 'A';

BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
