/** 	Retorna o número de sequência do exame
 	- Juliane Menin e Maicon Mueller -
	- 18/02/2008
*/
nr_sequencia_w			bigint := 0;
qtd_w				bigint := 0;
ie_restringe_result_w	 	varchar(1):= 'N';


BEGIN
select	count(*)
into STRICT 	qtd_w
from 	result_laboratorio
where 	nr_seq_prescricao 	= nr_seq_prescr_p
and 	nr_prescricao 	= nr_prescricao_p;


SELECT	obter_se_exame_bloqueado( nr_seq_prescr_p, nr_prescricao_p)
into STRICT	ie_restringe_result_w
;

if (qtd_w > 0 AND ie_restringe_result_w = 'N') then
	select 	max(nr_sequencia)
	into STRICT 	nr_sequencia_w
	from 	result_laboratorio
	where 	nr_seq_prescricao 	= nr_seq_prescr_p
	and 	nr_prescricao 	= nr_prescricao_p;
end if;

return nr_sequencia_w;

end;
