CREATE OR REPLACE FUNCTION obter_seq_result_lab ( nr_seq_prescr_p bigint, nr_prescricao_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w			bigint;
qtd_w				bigint := 0;


BEGIN
select	count(*)
into STRICT 	qtd_w
from 	result_laboratorio
where 	nr_seq_prescricao 	= nr_seq_prescr_p
and 	nr_prescricao 	= nr_prescricao_p;

if (qtd_w > 0) then
	select 	max(nr_sequencia)
	into STRICT 	nr_sequencia_w
	from 	result_laboratorio
	where 	nr_seq_prescricao 	= nr_seq_prescr_p
	and 	nr_prescricao 	= nr_prescricao_p;
end if;

return nr_sequencia_w;

end;
$body$
