CREATE OR REPLACE FUNCTION obter_seq_solic_vaga ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

 
nr_seq_reserva_w	bigint;


BEGIN 
 
select	coalesce(max(nr_sequencia),0) 
into STRICT	nr_seq_reserva_w 
from	gestao_vaga 
where	cd_pessoa_fisica		=	cd_pessoa_fisica_p 
and	dt_solicitacao 		<= 	clock_timestamp() 
and	((coalesce(nr_atendimento::text, '') = '') or (nr_atendimento IS NOT NULL AND nr_atendimento::text <> '' AND nr_seq_agenda IS NOT NULL AND nr_seq_agenda::text <> '')) 
and	ie_status in ('R','A');
 
return	nr_seq_reserva_w;
 
end;
