SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_seq_solucao_vipe (ie_opcao_p bigint, nr_atendimento_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, ds_item_p text) RETURNS bigint AS $body$
DECLARE

 
ds_solucao_w		varchar(255);
cd_pessoa_fisica_w	varchar(255);
nr_seq_solucao_w	bigint := 1;
nr_seq_solucao_ww	bigint;
nr_prescricao_w		bigint;
			
c01 CURSOR FOR 
SELECT	a.nr_prescricao, 
	a.nr_seq_solucao 
from	prescr_medica b, 
    prescr_solucao a 
where	a.nr_prescricao  = b.nr_prescricao 
and  	a.ie_suspenso   <> 'S' 
and  	b.ie_origem_inf  = '1' 
and  	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and	b.cd_pessoa_fisica = cd_pessoa_fisica_w 
and		ie_opcao_p = 4 

union all
 
SELECT	a.nr_prescricao, 
	a.nr_seq_solucao 
from	prescr_medica b, 
    prescr_solucao a 
where	b.nr_atendimento = nr_atendimento_p 
and   clock_timestamp() between b.dt_inicio_prescr and b.dt_validade_prescr 
and	a.nr_prescricao  = b.nr_prescricao 
and  	a.ie_suspenso   <> 'S' 
and  	b.ie_origem_inf  = '1' 
and  	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and		ie_opcao_p = 0 

union all
 
select	a.nr_prescricao, 
	a.nr_seq_solucao 
from	prescr_medica b, 
    prescr_solucao a 
where	b.nr_atendimento = nr_atendimento_p 
and	trunc(b.dt_prescricao) between(clock_timestamp() - interval '2 days') and clock_timestamp() 
and	a.nr_prescricao  = b.nr_prescricao 
and  	a.ie_suspenso   <> 'S' 
and  	b.ie_origem_inf  = '1' 
and  	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and		ie_opcao_p = 1 

union all
 
select	a.nr_prescricao, 
	a.nr_seq_solucao 
from	prescr_medica b, 
    prescr_solucao a 
where	b.nr_atendimento = nr_atendimento_p 
and 	trunc(b.dt_prescricao) between(clock_timestamp() - interval '7 days') and clock_timestamp() 
and	a.nr_prescricao  = b.nr_prescricao 
and  	a.ie_suspenso   <> 'S' 
and  	b.ie_origem_inf  = '1' 
and  	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and		ie_opcao_p = 2 

union all
 
select	a.nr_prescricao, 
	a.nr_seq_solucao 
from	prescr_medica b, 
    prescr_solucao a 
where	b.nr_atendimento = nr_atendimento_p 
and	a.nr_prescricao  = b.nr_prescricao 
and  	a.ie_suspenso   <> 'S' 
and  	b.ie_origem_inf  = '1' 
and  	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and		ie_opcao_p = 3 

union all
 
select	a.nr_prescricao, 
		a.nr_seq_solucao 
from  prescr_solucao a, 
		prescr_medica b 
where	a.nr_prescricao		= b.nr_prescricao 
and		b.nr_atendimento	= nr_atendimento_p 
and		b.dt_prescricao between dt_inicio_p and	dt_final_p 
--and		b.dt_validade_prescr between dt_inicio_p - 2 and dt_final_p + 2 
and  	a.ie_suspenso   <> 'S' 
and  	b.ie_origem_inf  = '1' 
and  	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and		ie_opcao_p = 5 
order by 1 desc;


BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
open C01;
loop 
fetch C01 into 
	nr_prescricao_w, 
	nr_seq_solucao_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_solucao_w	:= substr(obter_desc_solucao_adep(nr_prescricao_w,nr_seq_solucao_ww),1,255);
	if (ds_solucao_w	<> ds_item_p) then 
		nr_seq_solucao_w	:= nr_seq_solucao_w + 1;
	else 
		exit;
	end if;
end loop;
close C01;
 
return	nr_seq_solucao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_seq_solucao_vipe (ie_opcao_p bigint, nr_atendimento_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, ds_item_p text) FROM PUBLIC;

