SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_seq_tabela_assinada ( nr_seq_assinatura_p bigint, ie_opcao_p bigint default 0) RETURNS varchar AS $body$
DECLARE


nr_seq_proj_ass_w	bigint;
nr_seq_tabela_w		bigint;
ds_seq_tabela_w		varchar(255);

BEGIN

select	nr_seq_proj_ass
into STRICT	nr_seq_proj_ass_w
from	tasy_assinatura_digital
where	nr_sequencia = nr_seq_assinatura_p;

if (nr_seq_proj_ass_w = 1) then
	select	nr_prescricao,
		obter_desc_expressao(325814)/*'Prescrição: '*/
 || nr_prescricao
	into STRICT	nr_seq_tabela_w,
		ds_seq_tabela_w
	from	prescr_medica
	where	nr_seq_assinatura = nr_seq_assinatura_p;
elsif (nr_seq_proj_ass_w = 183) then
	select	cd_evolucao,
		obter_desc_expressao(728940)/*'Evolução: '*/
 || cd_evolucao
	into STRICT	nr_seq_tabela_w,
		ds_seq_tabela_w
	from	evolucao_paciente
	where	nr_seq_assinatura = nr_seq_assinatura_p;
elsif (nr_seq_proj_ass_w = 184) then
	select	nr_sequencia,
		obter_desc_expressao(298525)/*'Sinal vital: '*/
 ||': '|| nr_sequencia
	into STRICT	nr_seq_tabela_w,
		ds_seq_tabela_w
	from	atendimento_sinal_vital
	where	nr_seq_assinatura = nr_seq_assinatura_p;
end if;

if (ie_opcao_p = 0) then
	return nr_seq_tabela_w;
else
	return ds_seq_tabela_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
