CREATE OR REPLACE FUNCTION obter_seq_transf_pront (nr_seq_prontuario_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_transf_w	bigint;


BEGIN

if (coalesce(nr_seq_prontuario_p,0) > 0) then

	select 	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_transf_w
	from   	transf_prontuario a,
		transf_prontuario_envelope b
	where 	a.nr_sequencia = b.nr_seq_transf
	and 	b.nr_seq_prontuario = nr_seq_prontuario_p
	and 	coalesce(a.dt_recebimento::text, '') = '';
end if;

return	nr_seq_transf_w;

end;
