SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_servico_atendimento ( nr_atendimento_p atendimento_servico.nr_atendimento%type ) RETURNS varchar AS $body$
DECLARE


desc_servicos   varchar(255)  := null;

obtem_servicos CURSOR FOR
    SELECT      tsa.ds_servico
    from        atendimento_servico ats
    inner join  tipo_servico_atend tsa
    on          ats.nr_seq_servico = tsa.nr_sequencia
    where       ats.nr_atendimento = nr_atendimento_p
    group by    tsa.ds_servico;

BEGIN
	for servico in obtem_servicos loop
		begin
            if (coalesce(desc_servicos::text, '') = '') then
                desc_servicos := servico.ds_servico;
            else
                desc_servicos := desc_servicos || ', ' || servico.ds_servico;
            end if;
        end;
	end loop;

    return desc_servicos;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
