CREATE OR REPLACE FUNCTION obter_sessao_agenda_servico ( cd_pessoa_fisica_p text, cd_agenda_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sessao_total_w	smallint;
nr_sessao_atual_w	smallint;
ds_retorno_w		smallint;


BEGIN
select	max(qt_total_secao),
	max(nr_Secao) + 1
into STRICT	nr_sessao_total_w,
	nr_sessao_atual_w
from	agenda_consulta
where	cd_pessoa_fisica	= cd_pessoa_Fisica_p
and	cd_agenda		= cd_Agenda_p
and	ie_status_agenda not in ('B','C')
and	nr_sequencia	= (	SELECT	max(nr_sequencia)
				from	agenda_consulta
				where	cd_pessoa_fisica	= cd_pessoa_fisica_p
				and	cd_agenda		= cd_agenda_p
				and	ie_status_agenda not in ('B','C'));

if (ie_opcao_p	= 1) then
	ds_retorno_w	:= nr_sessao_atual_w;
else
	ds_retorno_w	:= nr_sessao_total_w;

end if;

if (ds_retorno_w	= 0) then
	ds_Retorno_w	:= '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
