SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_sessoes_geradas ( nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE



ie_retorno_w		varchar(1) := 'N';
ie_utiliza_cont_w	varchar(1);
nr_controle_secao_w	agenda_consulta.nr_controle_secao%Type;
cd_pessoa_fisica_w	agenda_consulta.cd_pessoa_fisica%Type;
nr_seq_proc_interno_w	agenda_consulta.nr_seq_proc_interno%Type;
nm_paciente_w		agenda_consulta.nm_paciente%Type;


BEGIN
ie_utiliza_cont_w	:= obter_valor_param_usuario(866, 104, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);

if (ie_utiliza_cont_w = 'N') then
	return ie_retorno_w;
end if;

if (coalesce(nr_seq_agenda_p,0) > 0) then

	select	max(nr_controle_secao),
			max(cd_pessoa_fisica),
			max(nr_seq_proc_interno),
			max(nm_paciente)
	into STRICT	nr_controle_secao_w,
			cd_pessoa_fisica_w,
			nr_seq_proc_interno_w,
			nm_paciente_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	agenda_consulta
	where	nr_controle_secao	= nr_controle_secao_w
	and	ie_status_agenda <> 'C'
	and		((cd_pessoa_fisica	= cd_pessoa_fisica_w) or (nm_paciente = nm_paciente_w))
	and		((coalesce(nr_seq_proc_interno, nr_seq_proc_interno_w)		= nr_seq_proc_interno_w) or (coalesce(nr_seq_proc_interno_w::text, '') = ''))
	and		nr_sequencia 		<> nr_seq_agenda_p;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_sessoes_geradas ( nr_seq_agenda_p bigint) FROM PUBLIC;

