CREATE OR REPLACE FUNCTION obter_setores_escala (nr_seq_escala_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_setor_w	varchar(20)	:= '';
ds_retorno_w	varchar(4000);

c01 CURSOR FOR 
	SELECT 	substr(obter_dados_setor(cd_setor_atendimento,'DO'),1,20) 
	from 	escala_setor 
	where	nr_seq_escala	= nr_seq_escala_p;
			

BEGIN 
 
open	c01;
loop 
fetch	c01 into ds_setor_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	ds_retorno_w	:= ds_retorno_w || ds_setor_w || '/';
 
	end;
end loop;
close c01;
 
ds_retorno_w	:= substr(ds_retorno_w, 1, length(ds_retorno_w) - 1);
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
