CREATE OR REPLACE FUNCTION obter_setor_atendimento_lib ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE



cd_setor_atendimento_w		integer;
nr_seq_atepacu_w		bigint;


BEGIN

select	coalesce(max(nr_seq_interno),0)
into STRICT	nr_seq_atepacu_w
from 	atend_paciente_unidade a,
	setor_atendimento b
where	a.nr_atendimento 		= nr_atendimento_p
and	a.cd_setor_atendimento		= b.cd_setor_atendimento
and	coalesce(b.ie_setor_lib_local,'S')	= 'S'
  and 	coalesce(a.dt_saida_unidade, a.dt_entrada_unidade + 9999)	=
	(SELECT max(coalesce(b.dt_saida_unidade, b.dt_entrada_unidade + 9999))
	from 	atend_paciente_unidade b,
		setor_atendimento c
	where 	b.nr_atendimento 		= nr_atendimento_p
	and	c.cd_setor_atendimento		= b.cd_setor_atendimento
	and	coalesce(c.ie_setor_lib_local,'S')	= 'S');

select	max(a.cd_setor_atendimento)
into STRICT	cd_setor_atendimento_w
from	atend_paciente_unidade a
where	a.nr_seq_interno = nr_seq_atepacu_w;

RETURN cd_setor_atendimento_w;

END;
