CREATE OR REPLACE FUNCTION obter_setor_atend_classi ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE



cd_setor_atendimento_w	bigint;


BEGIN
	
select	max(a.cd_setor_atendimento)
into STRICT	cd_setor_atendimento_w
from	setor_atendimento b,
	atend_paciente_unidade a
where	a.nr_atendimento		= nr_atendimento_p
and	a.cd_setor_atendimento	= b.cd_setor_atendimento
and	b.cd_classif_setor		in ('1','2','3','4')
and a.nr_seq_interno = (
	SELECT Obter_Atepacu_paciente(nr_atendimento_p, 'A')
	)
order by	1;	
	
/*
CD_CLASSIF_SETOR (1) - Pronto Socorro
CD_CLASSIF_SETOR (2) - Centro cirurgico
CD_CLASSIF_SETOR (3) - Unidades de internacao
CD_CLASSIF_SETOR (4) - Unidade de terapia intensiva
*/
	


RETURN cd_setor_atendimento_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
