CREATE OR REPLACE FUNCTION obter_setor_atend_nao_cancel ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
 
cd_setor_atendimento_w	integer;
dt_cancelamento_w timestamp;


BEGIN 
 
select	max(dt_cancelamento) 
into STRICT	dt_cancelamento_w 
from	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
if (coalesce(dt_cancelamento_w::text, '') = '') then 
	select	max(cd_setor_atendimento) 
	into STRICT	cd_setor_atendimento_w 
	from	atend_paciente_unidade 
	where	nr_seq_interno = ( 
		SELECT Obter_Atepacu_paciente(nr_atendimento_p, 'A') 
		);	
end if;
 
RETURN cd_setor_atendimento_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
