CREATE OR REPLACE FUNCTION obter_setor_atend_tab ( cd_setor_atendimento_p bigint, ie_opcao_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_retorno_p:
		CD ->	Código setor atendimento
		DS ->	Descrição setor atendimento
		ES ->	Código estabelecimento
*/
ds_retorno_w		varchar(100);
ds_setor_atend_w	varchar(100);
cd_setor_atend_w	integer;
cd_estab_atend_w	integer;

BEGIN

if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	select	cd_setor_atendimento,
		ds_setor_atendimento,
		cd_estabelecimento
	into STRICT	cd_setor_atend_w,
		ds_setor_atend_w,
		cd_estab_atend_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_p;


	if (ie_opcao_retorno_p = 'CD') then
		ds_retorno_w := cd_setor_atend_w;

	elsif (ie_opcao_retorno_p = 'DS') then
		ds_retorno_w := ds_setor_atend_w;

	elsif (ie_opcao_retorno_p = 'ES') then
		ds_retorno_w := cd_estab_atend_w;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
