CREATE OR REPLACE FUNCTION obter_setor_atual_gestao_doc ( cd_setor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_reg_w		bigint;


BEGIN

select	count(1)
into STRICT	qt_reg_w
from	gestao_documento a,
	gestao_doc_item d,
	solic_documento b,
	solic_item_documento c
where	b.nr_sequencia =		c.nr_seq_solic_doc
and	a.nr_sequencia =		d.nr_seq_gestao_doc
and	c.nr_seq_item_gestao_doc =	d.nr_sequencia
and	a.cd_setor_atendimento	=	cd_setor_p
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
and	coalesce(b.dt_baixa::text, '') = '';

if (coalesce(qt_reg_w,0) > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
