CREATE OR REPLACE FUNCTION obter_setor_atual_paciente ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_setor_w			bigint;


BEGIN

	select	coalesce(max(cd_setor_atendimento),0)
	into STRICT	cd_setor_w
	from 	atend_paciente_unidade a
	where	a.nr_atendimento 		= nr_atendimento_p
	and 	coalesce(a.dt_saida_unidade, a.dt_entrada_unidade + 9999)	=
			(SELECT max(coalesce(b.dt_saida_unidade, b.dt_entrada_unidade + 9999))
			from atend_paciente_unidade b
			where b.nr_atendimento 	= nr_atendimento_p);
		
return cd_setor_w;

end;
