CREATE OR REPLACE FUNCTION obter_setor_auditoria_pac ( nr_sequencia_p bigint, cd_setor_atendimento_p bigint, ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';


BEGIN

if (ie_tipo_p = 1) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	material_atend_paciente
	where	nr_sequencia = nr_sequencia_p
	and	cd_setor_atendimento = cd_setor_atendimento_p;
elsif (ie_tipo_p = 2) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	procedimento_paciente
	where	nr_sequencia = nr_sequencia_p
	and	cd_setor_atendimento = cd_setor_atendimento_p;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
