CREATE OR REPLACE FUNCTION obter_setor_banco_sangue ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE



cd_setor_atendimento_w		integer;
cd_setor_exclusivo_w		integer;

C01 CURSOR FOR
SELECT	a.cd_setor_atendimento
from	setor_atendimento b,
	proc_interno_setor a
where	a.nr_seq_proc_interno	= nr_seq_proc_interno_p
and	a.cd_setor_atendimento	= b.cd_setor_atendimento
and	b.cd_estabelecimento_base = cd_estabelecimento_p
and	coalesce(cd_perfil,obter_perfil_ativo) = obter_perfil_ativo
order by a.ie_banco_sangue_rep,
	 nr_prioridade desc;


C02 CURSOR FOR
	SELECT	a.cd_setor_atendimento
	from	setor_atendimento b,
		procedimento_setor_atend a
	where	a.cd_setor_atendimento	= b.cd_setor_atendimento
	and	b.cd_estabelecimento_base = cd_estabelecimento_p
	and	a.cd_procedimento	= cd_procedimento_p
	and	a.ie_origem_proced	= ie_origem_proced_p
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	order by a.ie_banco_sangue_rep,
		ie_prioridade desc;



BEGIN

begin
select	cd_setor_exclusivo
into STRICT	cd_setor_exclusivo_w
from	procedimento
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
when others then
	cd_setor_exclusivo_w	:= null;
end;

if (nr_seq_proc_interno_p > 0) then
	begin
	open C01;
	loop
		fetch C01 into
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		cd_setor_atendimento_w	:= cd_setor_atendimento_w;
	end loop;
	close C01;
	end;
else
	begin
	open C02;
	loop
		fetch C02 into
		cd_setor_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		cd_setor_atendimento_w	:= cd_setor_atendimento_w;
	end loop;
	close C02;
	end;
end if;

return	coalesce(cd_setor_exclusivo_w,cd_setor_atendimento_w);

END;
$body$
