SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_setor_coleta_dif (nr_atendimento_p bigint, cd_setor_coleta_p bigint) RETURNS varchar AS $body$
DECLARE


cd_setor_coleta_w 	bigint;
ds_retorno_w 		varchar(1)   := 'N';
ds_setor_atendimento_w 	varchar(150) := null;


BEGIN

select 	CASE WHEN coalesce(ou.cd_setor_coleta::text, '') = '' THEN 'N'  ELSE 'S' END ,
	ou.cd_setor_coleta,
	obter_desc_setor_atend(ou.cd_setor_coleta) ds_setor_atendimento
into STRICT 	ds_retorno_w,
	cd_setor_coleta_w,
	ds_setor_atendimento_w
from 	cpoe_order_unit ou
where 	ou.nr_sequencia = (
	SELECT max(x.nr_sequencia)  
		from  cpoe_order_unit x,
		      cpoe_tipo_pedido tp
		where x.nr_sequencia = ou.nr_sequencia
		and   x.nr_atendimento = ou.nr_atendimento
		and   tp.nr_sequencia = x.nr_seq_cpoe_tipo_pedido
		and   tp.nr_seq_sub_grp in ('B', 'L')
	)
and 	ou.nr_atendimento = nr_atendimento_p  LIMIT 1;
if (ds_retorno_w = 'S') then

	if (cd_setor_coleta_w <> cd_setor_coleta_p) then
		return ds_setor_atendimento_w;
	end if;
	
end if;

return	null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
