SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_setor_coleta_entr_exame ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

ds_retorno_w			integer;

/*
C = Coleta
E = Entrega
*/
BEGIN

if (ie_opcao_p = 'C') then

	select	max(cd_setor_coleta)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_prescricao_p;
else
	select	max(cd_setor_entrega)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_prescricao_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
