SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_setor_consite_proc (nr_atendimento_p bigint, ie_parametro_p text) RETURNS varchar AS $body$
DECLARE


ds_setor_atendimento_w		varchar(255);
cont_w				bigint;
/*
ie_parametro_p = verifica se deve utilizar a data de liberacao da enfermagem tamb√©m
*/
BEGIN

select	max(a.ds_setor_atendimento)
into STRICT	ds_setor_atendimento_w
from	setor_atendimento a,
	prescr_procedimento b,
	prescr_medica c
where	c.nr_prescricao			= b.nr_prescricao
and	b.cd_setor_atendimento		= a.cd_setor_atendimento
and	coalesce(a.ie_permite_alta_pend,'S')	= 'N'
and	c.nr_atendimento			= nr_atendimento_p
and	((coalesce(b.nr_seq_exame::text, '') = ''     and b.ie_status_execucao in ('10', '11', '12', '13', '14', '15')) or ((b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '') and b.ie_status_atend < 20))
and	coalesce(b.ie_suspenso,'N')		<> 'S'
and	coalesce(c.dt_suspensao::text, '') = ''
and	coalesce(b.dt_cancelamento::text, '') = ''
and	b.cd_motivo_baixa 			= 0
and	((c.dt_liberacao_medico IS NOT NULL AND c.dt_liberacao_medico::text <> '' AND c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '') or
	(ie_parametro_p = 'S' AND c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> ''))
and	c.dt_prescricao		> clock_timestamp() - interval '30 days';

if (coalesce(ds_setor_atendimento_w::text, '') = '') then
	select	max(a.ds_setor_atendimento)
	into STRICT	ds_setor_atendimento_w
	from	setor_atendimento a,
		prescr_procedimento b,
		prescr_medica c
	where	c.nr_prescricao		= b.nr_prescricao
	and	b.cd_setor_atendimento	= a.cd_setor_atendimento
	and	c.nr_atendimento		= nr_atendimento_p
	and	((coalesce(b.nr_seq_exame::text, '') = ''     and b.ie_status_execucao in ('10', '11', '12', '13', '14', '15')) or ((b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '') and b.ie_status_atend < 20))
	and	coalesce(b.ie_suspenso,'N')	<> 'S'
	and	coalesce(c.dt_suspensao::text, '') = ''
	and	coalesce(b.dt_cancelamento::text, '') = ''
	and	b.cd_motivo_baixa 		= 0
	and	c.dt_prescricao		> clock_timestamp() - interval '30 days'
	and	((c.dt_liberacao_medico IS NOT NULL AND c.dt_liberacao_medico::text <> '' AND c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '') or
		(ie_parametro_p = 'S' AND c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> ''))
	and	Obter_se_obriga_alta_proc(b.cd_procedimento, b.ie_origem_proced) = 'S';
end if;

return	ds_setor_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
