CREATE OR REPLACE FUNCTION obter_setor_data_gasto_sup ( nr_atendimento_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_material_w			double precision;
qt_procedimento_w		double precision;
ie_pendente_w			varchar(1)	:= 'N';

ds_retorno_w			varchar(4000);
ds_setor_w			varchar(90);

C01 CURSOR FOR
	SELECT  distinct
		subsTR(obter_nome_setor(CD_SETOR_ATENDIMENTO),1,90)
	from	procedimento_paciente
	where	nr_atendimento 	= nr_atendimento_p
	and	dt_procedimento	> dt_referencia_p
	and	coalesce(cd_motivo_exc_conta::text, '') = '';

C02 CURSOR FOR
	SELECT  distinct
		subsTR(obter_nome_setor(CD_SETOR_ATENDIMENTO),1,90)
	from	material_atend_paciente
	where	nr_atendimento 	= nr_atendimento_p
	and	dt_atendimento	> dt_referencia_p
	and	qt_material	>= 0
	and	coalesce(cd_motivo_exc_conta::text, '') = '';


BEGIN
ds_retorno_w	:= null;
open C01;
loop
fetch C01 into
	ds_setor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= ds_retorno_w || ds_setor_w || CHR(13);
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	ds_setor_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	ds_retorno_w	:= ds_retorno_w || ds_setor_w || CHR(13);
	end;
end loop;
close C02;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
