CREATE OR REPLACE FUNCTION obter_setor_entrega_prescr (nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_retorno_w	integer;
ds_retorno_w	varchar(100);


BEGIN

select	max(cd_setor_entrega),
	substr(obter_nome_setor(max(cd_setor_entrega)),1,100)
into STRICT	cd_retorno_w,
	ds_retorno_w
from	prescr_medica
where	nr_prescricao	=	nr_prescricao_p;

if (ie_opcao_p = 'C') then
	return	cd_retorno_w;
else
	return	ds_retorno_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
