CREATE OR REPLACE FUNCTION obter_setor_entrega_proc (cd_procedimento_p bigint, nr_seq_proc_interno_p bigint) RETURNS bigint AS $body$
DECLARE


cd_area_proc_w					regra_setor_entrega_proc.cd_grupo_proc%type;
cd_especialidade_proc_w			regra_setor_entrega_proc.cd_especialidade%type;
cd_grupo_proc_w					regra_setor_entrega_proc.cd_area_procedimento%type;
cd_setor_entrega_w				regra_setor_entrega_proc.cd_setor_atendimento%type;


BEGIN
if (cd_procedimento_p > 0) or (nr_seq_proc_interno_p > 0) then


	select  coalesce(max(obter_dados_estrut_proc(cd_procedimento,ie_origem_proced,'C','A')),0),
			coalesce(max(obter_dados_estrut_proc(cd_procedimento,ie_origem_proced,'C','E')),0),
			coalesce(max(cd_grupo_proc),0)
	into STRICT	cd_area_proc_w,
			cd_especialidade_proc_w,
			cd_grupo_proc_w
	from	procedimento
	where	cd_procedimento = cd_procedimento_p;

	select 	coalesce(max(cd_setor_atendimento),0)
	into STRICT	cd_setor_entrega_w
	from	regra_setor_entrega_proc
	where	coalesce(coalesce(cd_procedimento,cd_procedimento_p),0) = coalesce(cd_procedimento_p,0)
	and		coalesce(coalesce(nr_seq_proc_interno,nr_seq_proc_interno_p),0) = coalesce(nr_seq_proc_interno_p,0)
	and		coalesce(cd_area_procedimento,cd_area_proc_w) = cd_area_proc_w
	and		coalesce(cd_especialidade,cd_especialidade_proc_w) = cd_especialidade_proc_w
	and		coalesce(cd_grupo_proc,cd_grupo_proc_w) = cd_grupo_proc_w;

end if;

return	cd_setor_entrega_w;

end;
$body$
