CREATE OR REPLACE FUNCTION obter_setor_envio_comunic_usu (nr_seq_evento_p bigint) RETURNS varchar AS $body$
DECLARE



cd_setor_destino_w		varchar(2000);
cd_setor_regra_w		integer;


C01 CURSOR FOR
SELECT	distinct cd_setor_atendimento
from	regra_envio_comunic_usu
where	nr_seq_evento = nr_seq_evento_p
and	(cd_setor_atendimento IS NOT NULL AND cd_setor_atendimento::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	cd_setor_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_setor_destino_w := substr(cd_setor_destino_w || cd_setor_regra_w || ',',1,2000);
	end;
end loop;
close C01;

return	cd_setor_destino_w;

end;
$body$
