CREATE OR REPLACE FUNCTION obter_setor_excluir_kit ( nr_sequencia_kit_p bigint, cd_setor_atendimento_p bigint ) RETURNS varchar AS $body$
DECLARE

cd_setor_excluir_kit_w		bigint;
ie_excluir_w			varchar(1) := 'N';

C01 CURSOR FOR
	SELECT 	a.CD_SETOR_ATENDIMENTO
	from	SETOR_KIT_MAT_PRESCRICAO a
	where	a.NR_SEQ_REGRA = nr_sequencia_kit_p
	and		(a.CD_SETOR_ATENDIMENTO IS NOT NULL AND a.CD_SETOR_ATENDIMENTO::text <> '')
	
Union all

	SELECT 	a.cd_setor_excluir
	from 	KIT_MAT_PRESCRICAO a
	where	a.nr_sequencia = nr_sequencia_kit_p
	and		(a.cd_setor_excluir IS NOT NULL AND a.cd_setor_excluir::text <> '');

BEGIN

open C01;
loop
fetch C01 into
	cd_setor_excluir_kit_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(cd_setor_excluir_kit_w, 0) = cd_setor_atendimento_p) then
		ie_excluir_w := 'S';
	end if;
	end;
end loop;
close C01;

return	ie_excluir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
