CREATE OR REPLACE FUNCTION obter_setor_exec_item (nr_seq_item_p bigint, ie_tipo_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_setor_atendimento_w		varchar(100);


BEGIN

if (ie_tipo_item_p		= 1) then
	select	substr(obter_nome_setor(cd_setor_atendimento),1,100)
	into STRICT	ds_setor_atendimento_w
	from	material_atend_paciente
	where	nr_sequencia	= nr_seq_item_p;
else
	select	substr(obter_nome_setor(cd_setor_atendimento),1,100)
	into STRICT	ds_setor_atendimento_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_item_p;
end if;

return	ds_setor_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
