CREATE OR REPLACE FUNCTION obter_setor_integracao_dixtal ( cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		bigint;
ds_regra_w		varchar(255);


BEGIN

begin
	select	max(substr(ds_setor_cod_externo,1,255))
	into STRICT	ds_regra_w
	from	parametros_pepo
	where	cd_estabelecimento	= obter_estabelecimento_setor(cd_setor_atendimento_p);

	if (coalesce(ds_regra_w,'XPTO') <> 'XPTO'
		and Obter_Se_Contido(cd_setor_atendimento_p, ds_regra_w) =  'S') then
		begin
		select	max(cd_setor_externo)
		into STRICT	ds_retorno_w
		from	setor_atendimento
		where	cd_setor_atendimento = cd_setor_atendimento_p;
		end;
	else
		begin
		ds_retorno_w := cd_setor_atendimento_p;
		end;
	end if;

exception when others then
	ds_retorno_w := cd_setor_atendimento_p;
end;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
