CREATE OR REPLACE FUNCTION obter_setor_localizacao_laudo (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_localizacao_w		varchar(255) := '';
nr_seq_loc_w			bigint;


BEGIN

select	coalesce(max(nr_seq_loc),0)
into STRICT	nr_seq_loc_w
from	laudo_paciente_loc
where	nr_sequencia = nr_seq_laudo_p;

if (nr_seq_loc_w > 0) then

	select	substr(obter_nome_setor(cd_setor_atendimento),1,255)
	into STRICT	ds_localizacao_w
	from	laudo_paciente_loc
	where	nr_sequencia = nr_seq_laudo_p
	and	nr_seq_loc = nr_seq_loc_w;

end if;

return	ds_localizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
