CREATE OR REPLACE FUNCTION obter_setor_local_estoque (cd_local_estoque_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
C - Código
D - Descrição
*/
ds_setor_atendimento_w		varchar(255);
cd_setor_atendimento_w		bigint;
ds_retorno_w			varchar(255);


BEGIN

if (cd_local_estoque_p > 0) then
	begin

	select	max(cd_setor_atendimento),
		max(ds_setor_atendimento)
	into STRICT	cd_setor_atendimento_w,
		ds_setor_atendimento_w
	from    	setor_atendimento
	where   	cd_local_estoque = cd_local_estoque_p;

	end;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w := cd_setor_atendimento_w;
else
	ds_retorno_w := ds_setor_atendimento_w;
end if;

return	ds_retorno_w;

end;
$body$
