CREATE OR REPLACE FUNCTION obter_setor_mat_autor (nr_seq_mat_autor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
C - CÃ³dito do setor
N - Nome do setor
*/
ds_retorno_w			varchar(255);
nr_atendimento_w		bigint;
cd_material_w			bigint;
cd_setor_atendimento_w		bigint;


BEGIN

select	max(b.nr_atendimento),
	max(a.cd_material)
into STRICT	nr_atendimento_w,
	cd_material_w
from	autorizacao_cirurgia b,
	material_autor_cirurgia a
where	a.nr_sequencia		= nr_seq_mat_autor_p
and	a.nr_seq_autorizacao	= b.nr_sequencia;

ds_retorno_w	:= null;
if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') and (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
	select	max(cd_setor_atendimento)
	into STRICT	cd_setor_atendimento_w
	from	material_atend_paciente
	where	nr_atendimento		= nr_atendimento_w
	and	cd_material		= cd_material_w
	and	coalesce(cd_motivo_exc_conta::text, '') = '';

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= cd_setor_atendimento_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= obter_nome_setor(cd_setor_atendimento_w);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
