CREATE OR REPLACE FUNCTION obter_setor_prescr_proc (nr_prescricao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS bigint AS $body$
DECLARE


cd_setor_atendimento_w	PRESCR_MEDICA.CD_SETOR_ATENDIMENTO%type;
ie_setor_w				varchar(1);
cd_area_proc_w			REGRA_PROCED_SETOR_PRESCR.CD_AREA_PROCEDIMENTO%type;
cd_espec_proc_w			REGRA_PROCED_SETOR_PRESCR.CD_ESPECIALIDADE%type;
cd_grupo_Proc_w         REGRA_PROCED_SETOR_PRESCR.CD_GRUPO_PROC%type;

C01 CURSOR FOR
SELECT	IE_SETOR
from	REGRA_PROCED_SETOR_PRESCR
where	coalesce(cd_area_procedimento,coalesce(cd_area_proc_w,0))	= coalesce(cd_area_proc_w,0)
and	coalesce(cd_especialidade, coalesce(cd_espec_proc_w,0))		= coalesce(cd_espec_proc_w,0)
and	coalesce(cd_grupo_proc, coalesce(cd_grupo_proc_w,0))		= coalesce(cd_grupo_proc_w,0)
and	coalesce(cd_procedimento,cd_procedimento_p)		= cd_procedimento_p
and	coalesce(ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p;


BEGIN

select	max(cd_area_procedimento),
		max(cd_especialidade),
		max(cd_grupo_proc)
into STRICT	cd_area_proc_w,
		cd_espec_proc_w,
		cd_grupo_Proc_w
from	estrutura_Procedimento_v
where	cd_procedimento		= cd_procedimento_p
 and	ie_origem_proced	= ie_origem_proced_p;

open C01;
loop
fetch C01 into	
	ie_setor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_setor_w	:= ie_setor_w;
end loop;
close C01;

if (ie_setor_w = 'S') then
	select	max(cd_setor_atendimento)
	into STRICT	cd_setor_atendimento_w
	from 	prescr_medica
	where 	nr_prescricao = nr_prescricao_p;
	
end if;

return cd_setor_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
