CREATE OR REPLACE FUNCTION obter_setor_processo_gedipa ( cd_setor_prescricao_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


ie_regra_w				varchar(2) := null;
cd_setor_processo_w		integer;


BEGIN

select	max(ie_regra)
into STRICT	ie_regra_w
from	regra_setor_processo
where	cd_setor_prescricao	=	cd_setor_prescricao_p;

if (ie_regra_w IS NOT NULL AND ie_regra_w::text <> '') then
		if (ie_regra_w = 'SE') then
				select	coalesce(max(cd_setor_especifico),cd_setor_prescricao_p)
				into STRICT	cd_setor_processo_w
				from	regra_setor_processo
				where	cd_setor_prescricao	=	cd_setor_prescricao_p
				and		ie_regra			=	'SE';
		elsif (ie_regra_w = 'SP') then
				cd_setor_processo_w	:= Obter_Unidade_Atendimento(nr_atendimento_p,'IAA','CS');
		elsif (ie_regra_w = 'SR') then
				cd_setor_processo_w	:= cd_setor_prescricao_p;
		end if;
else
		cd_setor_processo_w	:= Obter_Unidade_Atendimento(nr_atendimento_p,'IAA','CS');
end if;

return	cd_setor_processo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
