CREATE OR REPLACE FUNCTION obter_setor_pront_pf (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


cd_setor_atendimento_w		integer;


BEGIN

select	coalesce(max(cd_setor_atendimento),0)
into STRICT	cd_setor_atendimento_w
from	pf_setor_prontuario
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	dt_inicio_cinculo <= clock_timestamp()
and	((coalesce(dt_final_vinculo::text, '') = '') or (dt_final_vinculo >= clock_timestamp()));

