CREATE OR REPLACE FUNCTION obter_setor_regras_ageint ( cd_tipo_agenda_p bigint, cd_agenda_p bigint, cd_procedimento_p bigint, ie_encaixe_p text, nm_usuario_p text, cd_estabelecimento_p bigint ) RETURNS bigint AS $body$
DECLARE


cd_setor_agendamento_w	setor_atendimento.cd_setor_atendimento%type;
ie_regra_geracao_setor_agend_w	varchar(1);


BEGIN
ie_regra_geracao_setor_agend_w := coalesce(obter_valor_param_usuario(820, 25, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p), 'U');
--630 ambos, se encaixe='N'
if (ie_encaixe_p = 'N') then
	if (cd_tipo_agenda_p = 2) then
		if (ie_regra_geracao_setor_agend_w <> 'N')then
			if (ie_regra_geracao_setor_agend_w = 'U')then
				select	coalesce(max(obter_setor_usuario(nm_usuario_p)), 0)
				into STRICT	cd_setor_agendamento_w
				;
			elsif (ie_regra_geracao_setor_agend_w = 'E') then
					select	coalesce(max(cd_setor_exclusivo), 0)
					into STRICT	cd_setor_agendamento_w
					from	agenda
					where	cd_agenda = cd_agenda_p;
			elsif (ie_regra_geracao_setor_agend_w = 'P') then
					select	coalesce(max(cd_setor_exclusivo), 0)
					into STRICT	cd_setor_agendamento_w
					from 	procedimento
					where 	cd_procedimento = cd_procedimento_p;
			end if;
		else
			cd_setor_agendamento_w := 0;
		end if;
--1046 se cd_tipo_agenda=3 (consultas)
	elsif (cd_tipo_agenda_p = 3) then
			select	coalesce(max(cd_setor_exclusivo), 0)
			into STRICT	cd_setor_agendamento_w
			from	agenda
			where	cd_agenda = cd_agenda_p;

			if (cd_setor_agendamento_w = 0) then
				select	coalesce(max(cd_setor_agenda), 0)
				into STRICT	cd_setor_agendamento_w
				from	agenda
				where	cd_agenda = cd_agenda_p;
			end if;

			if (cd_setor_agendamento_w = 0) then
				select	coalesce(max(obter_setor_usuario(nm_usuario_p)), 0)
				into STRICT	cd_setor_agendamento_w
				;
			end if;
	end if;
--1470 se encaixe
else
	cd_setor_agendamento_w := 0;
	if (cd_tipo_agenda_p 	= 3) then
		select	coalesce(max(cd_setor_exclusivo), 0)
		into STRICT	cd_setor_agendamento_w
		from	agenda
		where	cd_agenda = cd_agenda_p;

		if (cd_setor_agendamento_w = 0) then
			select	coalesce(max(obter_setor_usuario(nm_usuario_p)), 0)
			into STRICT	cd_setor_agendamento_w
			;
		end if;
	elsif (cd_tipo_agenda_p = 2) then -- AGENDA EXAMES
		if (ie_regra_geracao_setor_agend_w <> 'N')then -- PARÂMETRO 25 (Não gera setor)
			if (ie_regra_geracao_setor_agend_w = 'U')then -- PARÂMETRO 25 (Setor do usuário)
				select	coalesce(max(obter_setor_usuario(nm_usuario_p)), 0)
				into STRICT	cd_setor_agendamento_w
				;
			elsif (ie_regra_geracao_setor_agend_w = 'E') then -- PARÂMETRO 25 (Setor exclusivo da agenda)
					select	coalesce(max(cd_setor_exclusivo), 0)
					into STRICT	cd_setor_agendamento_w
					from	agenda
					where	cd_agenda = cd_agenda_p;
			elsif (ie_regra_geracao_setor_agend_w = 'P') then -- PARÂMETRO 25 (Setor exclusivo do procedimento)
					select	coalesce(max(cd_setor_exclusivo), 0)
					into STRICT	cd_setor_agendamento_w
					from 	procedimento
					where 	cd_procedimento = cd_procedimento_p;
			end if;
		end if;
	end if;
end if;
if (cd_setor_agendamento_w = 0) then
	cd_setor_agendamento_w := null;
end if;


return	cd_setor_agendamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
