CREATE OR REPLACE FUNCTION obter_setor_resp_pend (nr_seq_pendencia_p bigint) RETURNS bigint AS $body$
DECLARE


cd_setor_resp_pend_w		integer;
nr_seq_estagio_w		bigint;
nr_seq_regra_resp_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_setor_atendimento_w		integer;


BEGIN

cd_setor_resp_pend_w:= 0;

select 	nr_seq_regra_resp,
	nr_seq_estagio
into STRICT	nr_seq_regra_resp_w,
	nr_seq_estagio_w
from 	cta_pendencia
where 	nr_sequencia = nr_seq_pendencia_p;

select 	max(cd_setor_atendimento),
	max(cd_pessoa_fisica)
into STRICT	cd_setor_atendimento_w,
	cd_pessoa_fisica_w
from 	cta_regra_resp_item
where 	nr_seq_regra = nr_seq_regra_resp_w
and 	nr_seq_estagio = nr_seq_estagio_w;

if (coalesce(cd_pessoa_fisica_w,'0') <> '0') then

	select 	max(cd_setor_atendimento)
	into STRICT	cd_setor_resp_pend_w
	from 	usuario
	where 	cd_pessoa_fisica = cd_pessoa_fisica_w;

elsif (coalesce(cd_setor_atendimento_w,0) <> 0) then

	cd_setor_resp_pend_w:= cd_setor_atendimento_w;

end if;

return	cd_setor_resp_pend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
