CREATE OR REPLACE FUNCTION obter_set_exec_proc_interno_jp ( nr_seq_proc_interno_p bigint, cd_setor_origem_p bigint, ie_tipo_atendimento_p bigint, ie_proced_principal_p text default 'S') RETURNS bigint AS $body$
DECLARE


cd_setor_atendimento_w	proc_interno_setor.cd_setor_atendimento%type;
cd_estabelecimento_w	proc_interno_setor.cd_estabelecimento%type;
nr_seq_agrupamento_w	setor_atendimento.nr_seq_agrupamento%type;
cd_medico_w				medico.cd_pessoa_fisica%type;

C01 CURSOR FOR
	SELECT	cd_setor_atendimento
	from	proc_interno_setor
	where	nr_seq_proc_interno	= nr_seq_proc_interno_p
	and	((cd_setor_origem	= cd_setor_origem_p) or (coalesce(cd_setor_origem_p::text, '') = '') or (coalesce(cd_setor_origem::text, '') = ''))
	and (coalesce(cd_estabelecimento,1) =  coalesce(cd_estabelecimento_w,1))
	and 	coalesce(ie_tipo_atendimento,0) =  coalesce(ie_tipo_atendimento_p,0)
	and	coalesce(cd_perfil,obter_perfil_ativo) = obter_perfil_ativo
	AND ((coalesce(ie_somente_principal,'N') = 'N') OR
		((coalesce(ie_somente_principal,'N') = 'S') AND (ie_proced_principal_p = 'S')))
	and	coalesce(nr_seq_agrupamento, 0) = coalesce(nr_seq_agrupamento_w,0)
	and		((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_medico_w,cd_especialidade) = 'S'))
	order by
		nr_prioridade desc,
		coalesce(cd_setor_origem,0),
		coalesce(ie_tipo_atendimento,0),
		coalesce(nr_seq_agrupamento,0);

BEGIN
	cd_estabelecimento_w :=	wheb_usuario_pck.get_cd_estabelecimento;

	select	coalesce(max(nr_seq_agrupamento),0)
	into STRICT	nr_seq_agrupamento_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_origem_p;

	select	max(a.cd_pessoa_fisica)
	into STRICT	cd_medico_w
	from	medico a,
			usuario b
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and		b.nm_usuario		= wheb_usuario_pck.get_nm_usuario;

	for C01_w in C01
	loop
		cd_setor_atendimento_w	:= C01_w.cd_setor_atendimento;
	end loop;

	return	cd_setor_atendimento_w;

END;
$body$
