CREATE OR REPLACE FUNCTION obter_sexo_leito_temporario (cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) RETURNS varchar AS $body$
DECLARE

    nr_pac_fem_w	smallint;
    nr_pac_masc_w	smallint;
    nr_ind_ambos_w	smallint;
    nr_ind_fem_w	smallint;
    nr_ind_masc_w	smallint;
    nr_unidades_w	integer;
    retorno_w       varchar(1) := NULL;


BEGIN
    BEGIN
        SELECT SUM(CASE WHEN ie_sexo_paciente='F' THEN  1  ELSE 0 END ),
               SUM(CASE WHEN ie_sexo_paciente='M' THEN  1  ELSE 0 END ),
               SUM(CASE WHEN ie_sexo_paciente='A' THEN  1  ELSE 0 END ),
               SUM(CASE WHEN ie_sexo_paciente='f' THEN  1  ELSE 0 END ),
               SUM(CASE WHEN ie_sexo_paciente='m' THEN  1  ELSE 0 END ),
               COUNT(*)
        INTO STRICT    nr_pac_fem_w,
                nr_pac_masc_w,
                nr_ind_ambos_w,
                nr_ind_fem_w,
                nr_ind_masc_w,
                nr_unidades_w
        FROM    unidade_atendimento
        WHERE   cd_setor_atendimento = cd_setor_atendimento_p
        AND     cd_unidade_basica	 = cd_unidade_basica_p
        AND     ie_sexo_fixo		 = 'N';
    EXCEPTION
        WHEN OTHERS THEN
            nr_pac_fem_w	:= 0;
            nr_pac_masc_w	:= 0;
            nr_ind_ambos_w	:= 0;
            nr_ind_fem_w	:= 0;
            nr_ind_masc_w	:= 0;
            nr_unidades_w	:= 0;
    END;

    IF (nr_unidades_w <> 0) THEN
        IF((nr_pac_fem_w <> 0) OR (nr_ind_fem_w <> 0)) AND (nr_pac_masc_w = 0 AND nr_ind_masc_w = 0) THEN
            retorno_w := 'f';
        ELSIF(nr_pac_fem_w = 0 AND nr_ind_fem_w = 0) AND ((nr_pac_masc_w <> 0) OR (nr_ind_masc_w <> 0)) THEN
            retorno_w := 'm';
        ELSIF((nr_pac_fem_w <> 0) OR (nr_ind_fem_w <> 0)) AND ((nr_pac_masc_w <> 0) OR (nr_ind_masc_w <> 0)) THEN
            retorno_w := 'A';
        END IF;
        --
        RETURN retorno_w;
        --
    END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
