CREATE OR REPLACE FUNCTION obter_sexo_paciente ( nr_atendimento_p integer ) RETURNS varchar AS $body$
DECLARE

    v_sexo_w varchar(20);

BEGIN
    if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
        select
            MAX(ie_sexo) as sexo
        into STRICT v_sexo_w
        from
            pessoa_fisica          pf,
            atendimento_paciente   at
        where
            at.cd_pessoa_fisica = pf.cd_pessoa_fisica
            and at.nr_atendimento = nr_atendimento_p;

    end if;

    return v_sexo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
