CREATE OR REPLACE FUNCTION obter_sexo_pac_leito ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) RETURNS varchar AS $body$
DECLARE

 
ie_leito_disp_w		varchar(1);
ie_retorno_w		varchar(1);


BEGIN 
 
select	Obter_Se_Leito_Disp(cd_setor_atendimento_p, cd_unidade_basica_p, cd_unidade_compl_p) 
into STRICT	ie_leito_disp_w
;
 
if (ie_leito_disp_w = 'S') then 
	ie_retorno_w	:= '';
elsif (ie_leito_disp_w = 'N') then 
 
	select	max(a.ie_sexo) 
	into STRICT	ie_retorno_w 
	from	unidade_atendimento c, 
		atendimento_paciente b, 
		pessoa_fisica a 
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
	and	b.nr_atendimento	= c.nr_atendimento 
	and	c.cd_setor_atendimento	= cd_setor_atendimento_p 
	and	cd_unidade_basica	= cd_unidade_basica_p 
	and	cd_unidade_compl	= cd_unidade_compl_p;
 
	if (coalesce(ie_retorno_w::text, '') = '') then 
		select	max(a.ie_sexo) 
		into STRICT	ie_retorno_w 
		from	unidade_atendimento c, 
			pessoa_fisica a 
		where	(c.cd_paciente_reserva IS NOT NULL AND c.cd_paciente_reserva::text <> '') 
		and	a.cd_pessoa_fisica	= c.cd_paciente_reserva 
		and	c.cd_setor_atendimento	= cd_setor_atendimento_p 
		and	c.cd_unidade_basica	= cd_unidade_basica_p 
		and	c.cd_unidade_compl	= cd_unidade_compl_p;
	end if;
 
end if;
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
