CREATE OR REPLACE FUNCTION obter_sexo_pf_cod ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ie_sexo_w	varchar(1);
ds_retorno_w	varchar(100);
cd_retorno_w	 bigint;

BEGIN

select	coalesce(max(ie_sexo),'X')
into STRICT	ie_sexo_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (ie_sexo_w <> 'X') then
	begin

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= ie_sexo_w;
	end if;

	end;
else
	ds_retorno_w := 'N';
end if;

select	CASE WHEN ds_retorno_w='M' THEN 1 WHEN ds_retorno_w='F' THEN 2 WHEN ds_retorno_w='I' THEN 3 WHEN ds_retorno_w='N' THEN 4 END
into STRICT	cd_retorno_w
;

return cd_retorno_w;


end;
$body$
